import React, { useState, useEffect } from 'react'
import { Progress, Button } from '@weekday/elements'

export default function PollComponent(props) {
  const [complete, setComplete] = useState(false)
  const [totalAnswers, setTotalAnswers] = useState(false)

  useEffect(() => {
    props.questions.map(question => {
      question.answers.map(answer => {
        if (answer.user_id == props.currentUserId) setComplete(true)
        setTotalAnswers(totalAnswers + 1)
      })
    })
  }, [])

  return (
    <React.Fragment>
      <style jsx>{`
        .poll-container {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: stretch;
          align-content: center;
          justify-content: center;
        }

        .poll-inner {
          flex: 1;
          flex-direction: column;
          display: flex;
          align-items: center;
          align-content: center;
          justify-content: center;
        }

        .progress-container {
          margin-bottom: 5px;
          width: 100%;
        }
      `}</style>

      <div className="poll-container">
        <div className="poll-inner">
          <div className="h5 color-d3 text-left w-100 mb-10">{props.title}</div>
          <div className="p color-d0 text-left w-100 mb-10">{props.description}</div>

          {!complete &&
            <React.Fragment>
              {props.questions.map((question, index) => {
                return (
                  <div className="progress-container" key={index}>
                    <Button
                      text={question.question}
                      theme="blue-border"
                      size="full-width"
                      style={{ height: 35 }}
                    />
                  </div>
                )
              })}
            </React.Fragment>
          }

          {complete &&
            <React.Fragment>
              {props.questions.map((question, index) => {
                return (
                  <div className="progress-container" key={index}>
                    const percentage = Math.floor((question.answers.length / totalAnswers) * 100) / 100
                    const color = '#e9edf2'

                    <Progress
                      percentage={percentage}
                      color={color}
                      text={question.question}
                      labels={true}
                    />
                  </div>
                )
              })}
            </React.Fragment>
          }

          <div className="p color-d1 text-left w-100 mt-5">
            <span>This poll expires in 5 days</span>
            {props.currentUserId == props.userId &&
              <strong className="button ml-10 color-blue">Edit poll</strong>
            }
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}
