import React, { useState, useEffect } from 'react'
import { useRouter, withRouter } from 'next/router'
import Head from 'next/head'
import fetch from 'isomorphic-unfetch'
import { Button, Error, Loading, Notification, Spinner } from '@weekday/elements'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import withData from '../config'
import PollComponent from '../components/poll.component'
import { useMutation, useSubscription } from '@apollo/react-hooks'
import { openAppModal } from '../util'

const ADD_VOTE = gql`
  mutation add_vote($objects: [poll_votes_insert_input!]!) {
    insert_poll_votes(objects: $objects) {
      returning {
        id
      }
    }
  }
`;

function Index(props) {
  // ?userId=5db7e3c98476242154d43181&channelId=5db87f04db059a6d8dc8d068
  const { router: { query }} = props
  const [userId, setUserId] = useState(query.userId)
  const [channelId, setChannelId] = useState(query.channelId)
  const [addVote, addVoteData] = useMutation(ADD_VOTE)
  const { loading, error, data } = useSubscription(gql`
    subscription {
      polls(where: { channel_id: { _eq: "${channelId}" } }) {
        id
        title
        description
        user_id
        channel_id
        expiry
        options
        poll_votes {
          option_id
          user_id
        }
      }
    }
  `)

  return (
    <React.Fragment>
      <Head>
        <title>Polls</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        <link href="/static/css/styles.css" rel="stylesheet" />
        <link href="/static/images/favicon.png" rel="shortcut icon" />
      </Head>

      <style global jsx>{`
        * {
          margin: 0px;
          padding: 0px;
        }

        body {
          background: white;
        }

        .container {
          background: white;
          width: 100%;
          height: 100%;
          position: absolute;
          left: 0px;
          top: 0px;
          display: flex;
          align-items: stretch;
          align-content: center;
          justify-content: center;
        }

        .error {
          position: absolute;
          top: 0px;
          left: 0px;
          width: 100%;
        }

        .polls-listing-container {
          flex: 1;
          display: flex;
          flex-direction: column;
          align-items: center;
          align-content: center;
          justify-content: flex-start;
          padding: 20px;
        }
      `}</style>

      <div className="container column">
        <div className="polls-listing-container">
          {(loading || !data) && <Spinner />}
          {((error || !data) && !loading) && <div className="error"><Error message="Error loading polls" /></div>}
          {data &&
            <React.Fragment>
              {(data.polls.length == 0) &&
                <React.Fragment>
                  <img src="../static/images/no-polls.png" width="60%" className="mb-30"/>
                  <div className="h3 mb-20 pl-20 pr-20 color-d2 text-center">There are no polls</div>
                  <div className="h5 mb-20 pl-20 pr-20 color-d0 text-center">There are no polls for this channel. Click on the button below to create your first poll.</div>
                </React.Fragment>
              }

              {data.polls.map((poll, index) => {
                return (
                  <PollComponent
                    tools={true}
                    key={index}
                    id={poll.id}
                    expiry={poll.expiry}
                    channelId={channelId}
                    userId={userId}
                    title={poll.title}
                    userId={poll.user_id}
                    description={poll.description}
                    options={poll.options || []}
                    pollVotes={poll.poll_votes || []}
                    currentUserId={userId}
                    onSubmit={(optionId) => {
                      addVote({
                        variables: {
                          objects: [
                            {
                              option_id: optionId,
                              poll_id: pollId,
                              user_id: userId,
                            }
                          ]
                        }
                      })
                    }}
                  />
                )
              })}
            </React.Fragment>
          }

          <div className="row justify-content-center mt-30 w-100">
            <Button
              size="small"
              theme="blue-border"
              text="Create a new poll"
              onClick={() => openAppModal({
                name: 'Create a poll',
                url: 'http://localhost:3000/create',
              })}
            />
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}

export default withData(withRouter(Index))
