import React, { useState, useEffect } from 'react'
import { Progress, Button, Input, TextArea } from '@weekday/elements'
import moment from 'moment'

export default function FormComponent(props) {
  const today = moment()

  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [day, setDay] = useState(today.format('DD'))
  const [month, setMonth] = useState(today.format('MM'))
  const [year, setYear] = useState(today.format('YYYY'))
  const [questions, setQuestions] = useState([])

  const answerQuestion = async (questionId) => {
    console.log(questionId)
    setComplete(true)
  }

  useEffect(() => {
    if (!props.id) return

    const date = moment(props.expiry)

    setTitle(props.title)
    setDescription(props.description)
    setDay(date.format('DD'))
    setMonth(date.format('MM'))
    setYear(date.format('YYYY'))
    setQuestions(props.questions)
  }, [])

  return (
    <React.Fragment>
      <style jsx>{`
        .poll-container {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: stretch;
          align-content: center;
          justify-content: center;
        }

        .poll-inner {
          flex: 1;
          flex-direction: column;
          display: flex;
          align-items: center;
          align-content: center;
          justify-content: center;
        }

        .progress-container {
          margin-bottom: 5px;
          width: 100%;
        }
      `}</style>

      <div className="poll-container">
        <div className="poll-inner">
          <div className="h4 color-d3 text-left w-100 mb-0">{props.title}</div>
          <div className="h5 color-d0 text-left w-100 mb-10">{props.description}</div>

          <Input
            value="ed"
          />

          {!complete && !expired &&
            <React.Fragment>
              {props.questions.map((question, index) => {
                return (
                  <div className="progress-container" key={index}>
                    <Button
                      text={question.question}
                      theme="blue-border"
                      size="full-width"
                      style={{ height: 35 }}
                      onClick={() => answerQuestion(question.id)}
                    />
                  </div>
                )
              })}
            </React.Fragment>
          }

          {(complete || expired) &&
            <React.Fragment>
              {props.questions.map((question, index) => {
                const percentage = Math.floor((question.answers.length / total) * 100)
                const color = question.answers.length >= highest ? '#e9edf2' : '#f0f2f5'

                return (
                  <div className="progress-container" key={index}>
                    <Progress
                      percentage={percentage}
                      color={color}
                      text={question.question}
                      labels={true}
                    />
                  </div>
                )
              })}
            </React.Fragment>
          }

          <div className="p color-d1 text-left w-100 mt-5">
            {expired &&
              <span>This poll expired {moment(props.expiry).fromNow()}</span>
            }
            {!expired &&
              <span>This poll expires at {moment(props.expiry).format('LLL')}</span>
            }
            {props.currentUserId == props.userId &&
              <strong className="button ml-10 color-blue">Update</strong>
            }
            {props.currentUserId == props.userId &&
              <strong className="button ml-10 color-red">Delete</strong>
            }
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}
