import React, { useState, useEffect } from 'react'
import { Progress, Button, Input, Textarea } from '@weekday/elements'
import moment from 'moment'

export default function FormComponent(props) {
  const today = moment()

  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [day, setDay] = useState(today.format('DD'))
  const [month, setMonth] = useState(today.format('MM'))
  const [year, setYear] = useState(today.format('YYYY'))
  const [questions, setQuestions] = useState([])

  const answerQuestion = async (questionId) => {
    console.log(questionId)
    setComplete(true)
  }

  useEffect(() => {
    if (!props.id) return

    const date = moment(props.expiry)

    setTitle(props.title)
    setDescription(props.description)
    setDay(date.format('DD'))
    setMonth(date.format('MM'))
    setYear(date.format('YYYY'))
    setQuestions(props.questions)
  }, [])

  return (
    <React.Fragment>
      <style jsx>{`
        .poll-container {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: stretch;
          align-content: center;
          justify-content: center;
        }

        .poll-inner {
          flex: 1;
          flex-direction: column;
          display: flex;
          align-items: center;
          align-content: center;
          justify-content: center;
        }

        .progress-container {
          margin-bottom: 5px;
          width: 100%;
        }
      `}</style>

      <div className="poll-container">
        <div className="poll-inner">
          <div className="h4 color-d3 text-left w-100 mb-0">Create a new poll</div>


          <Input
            value={title}
            inputSize="large"
            onChange={e => setTitle(e.target.value)}
          />

          <Textarea
            value={description}
            textareaSize="large"
            rows={3}
            onChange={e => setDescription(e.target.value)}
          />

        <div className="h5 color-d0 text-left w-100 mb-10">Questions</div>



        </div>
      </div>
    </React.Fragment>
  )
}
